// Type definitions for CSS imports
declare module '*.css' {
    const content: { [className: string]: string }
    export default content
}

declare module '*.scss' {
    const content: { [className: string]: string }
    export default content
}

declare module '*.sass' {
    const content: { [className: string]: string }
    export default content
}

// Type definitions for media files
declare module '*.mp4' {
    const src: string
    export default src
}

declare module '*.webm' {
    const src: string
    export default src
}

declare module '*.ogg' {
    const src: string
    export default src
}

